import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { setUserAllInto } from '../store/userStore';
import { url } from '../store/ref';
import '../styles/Header.scss';
import signInIcon from '../assets/icons/SingIn.svg';
import logo from '../assets/logo.png';

const Header = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user.user); // 리덕스에서 유저 정보 가져오기
  const userID = user ? user.userID : null;
  const [menuOpen, setMenuOpen] = useState(false); // 메뉴 상태 관리

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const logout = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${url}/logout`, {
        method: 'POST',
        credentials: 'include',
      });
      if (response.ok) {
        dispatch(setUserAllInto(null)); // 로그아웃 시 유저 정보 초기화
      } else {
        console.error('Failed to logout:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to logout:', error);
    }
  };

  return (
    <header className="mw">
      <nav>
        <h1>
          <Link to="/">
            <img src={logo} alt="Logo" />
          </Link>
        </h1>

        <FontAwesomeIcon 
          icon="fa-solid fa-bars" 
          className="menu-icon" 
          onClick={toggleMenu} 
        />

        <ul className={`menu ${menuOpen ? 'open' : ''}`}>
          <li>
            <Link to="/recipes">Recipes</Link>
          </li>
          <li>
            <Link to="/create">Share your recipe</Link>
          </li>
          {userID ? (
            <>
              <li>
                <Link to="/mypage">{userID}님 입장</Link>
              </li>
              <li>
                <Link to="/" onClick={logout}>
                  Logout
                </Link>
              </li>
            </>
          ) : (
            <li className="signIn">
              <img src={signInIcon} alt="Sign In" />
              <Link to="/login">Sign in</Link>
            </li>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default React.memo(Header);
