import style from '../css/CommentsArea.module.css';
import CommentsLi from './CommentsLi';
import { url } from '../store/ref';

// V 1. 로그인 된 유저만 댓글 입력란을 볼 수 있다.
// V 1-2 로그인이 안된 상태라면 로그인하세요라는 메시지를 보여준다.
// 1-3 댓글 입력 후 버튼 클릭하면 api 백엔드 개발
// 1-4 전송되는 정보 content postid (username- cookie 정보로 활용 즉, 백엔드에서 처리

const CommentsArea = ({ userName, postId }) => {
  const commentAdd = () => {
    const content = document.getElementById('content').value;

    const data = new FormData();
    data.set('postId', postId);
    data.set('content', content);

    console.log(data);

    const response = fetch(`${url}/commentAdd`, {
      method: 'POST',
      body : data,
      credentials: 'include', //쿠키 활용할때
    })
    log
    if (response.ok) {
      console.log('댓글 입력 완료!');
    }

    // fetch(`${url}/commentAdd`, {
    //   method: 'POST',
    //   headers: { ContentType: 'application/json' },
    //   body: JSON.stringify({
    //     postId,
    //     content,
    //   }),
    // }).then();
  };

  return (
    <section className={style.commentsArea}>
      <h3>댓글영역</h3>
      {userName ? (
        <div>
          <textarea name="content" id="content"></textarea>
          <button onClick={commentAdd}>댓글입력</button>
        </div>
      ) : (
        <p>로그인 하면 댓글을 등록할 수 있습니다.</p>
      )}

      <hr />
      <ul>
        <CommentsLi />
        <CommentsLi />
        <CommentsLi />
        <CommentsLi />
        <CommentsLi />
      </ul>
    </section>
  );
};

export default CommentsArea;
