// 스와이퍼
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import '../styles/Slide.module.scss';

import { Autoplay, Pagination, Navigation } from 'swiper/modules';
// 이미지 임포트
import slide1 from '../assets/mainSlide/Slide1.png';
import slide2 from '../assets/mainSlide/Slide2.png';
import slide3 from '../assets/mainSlide/Slide3.png';
import slide4 from '../assets/mainSlide/Slide4.png';

const slidesContent = [
  {
    image: slide1,
    title: "Strawberry Greek Yogurt",
    description: "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text.",
    frameClass: "frame5448" // 추가적인 스타일을 원한다면 프레임 클래스
  },
  {
    image: slide2,
    title: "Sweet Crepe",
    description: "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text.",
    frameClass: "frame5449"
  },
  {
    image: slide3,
    title: "Organic Market",
    description: "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text.",
    frameClass: "frame5461"
  },
  {
    image: slide4,
    title: "Sweet Pumpkin Soup",
    description: "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text.",
    frameClass: "frame5462"
  }
];

const Slide = () => {
  return (
    <Swiper
      spaceBetween={30}
      centeredSlides={true}
      autoplay={{
        delay: 2500,
        disableOnInteraction: false,
      }}
      pagination={{
        clickable: true,
      }}
      navigation={true}
      modules={[Autoplay, Pagination, Navigation]}
      className="mySwiper"
    >
      {slidesContent.map((slide, index) => (
        <SwiperSlide key={index} className={slide.frameClass}>
          <div className="slideContent">
            <img className="slideImg" src={slide.image} alt={`Slide ${index + 1}`} />
            <div className="textOverlay">
              <h2>{slide.title}</h2>
              <p>{slide.description}</p>
            </div>
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
};

export default Slide;
