import { useEffect, useState } from 'react';
import PostCard from '../components/PostCard';
import '../styles/MainListPage.scss';
// 스와이퍼
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import { Autoplay, Pagination, Navigation } from 'swiper/modules';

import { url } from '../store/ref';

// 이미지 임포트
import slide1 from '../assets/mainSlide/Slide1.png';
import slide2 from '../assets/mainSlide/Slide2.png';
import slide3 from '../assets/mainSlide/Slide3.png';
import slide4 from '../assets/mainSlide/Slide4.png';

const images = [slide1, slide2, slide3, slide4];

const MainListPage = () => {
  const [postList, setPostList] = useState([]);

  useEffect(() => {
    fetch(`${url}/postList`)
      .then((res) => res.json())
      .then((data) => setPostList(data))
      .catch((error) => {
        console.error('Error fetching posts:', error);
      });
  }, []);

  return (
    <>
      <Swiper
        spaceBetween={30}
        centeredSlides={true}
        autoplay={{
          delay: 2500,
          disableOnInteraction: false,
        }}
        pagination={{
          clickable: true,
        }}
        navigation={true}
        modules={[Autoplay, Pagination, Navigation]}
        className="mySwiper"
      >
        {images.map((image, index) => (
          <SwiperSlide key={index}>
            <img className="slideImg" src={image} alt={`Slide ${index + 1}`} />
          </SwiperSlide>
        ))}
      </Swiper>

      <main className="mw mainList">
        <h5>NEW RECIPES</h5>

        <div className="postsCon">
          {postList.map((post) => (
            <PostCard key={post._id} post={post} />
          ))}
        </div>
        <button className='btn-primary View' > View More </button>
      </main>
    </>
  );
};

export default MainListPage;
