import React, { useState } from 'react';
import { Virtual, Navigation, Pagination } from 'swiper/modules';
import { Swiper, SwiperSlide } from 'swiper/react';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';

const SpecialtySlide = () => {
  const [swiperRef, setSwiperRef] = useState(null);

  const slides = [
    { id: 1, title: 'Strawberry Crepe', image: '../assets/specialtySlide/specialty1.' },
    { id: 2, title: 'Organic Market', image: 'path/to/slide2.jpg' },
    { id: 3, title: 'Pumpkin Soup', image: 'path/to/slide3.jpg' },
    // 추가 슬라이드들
  ];

  return (
    <>
      <Swiper
        modules={[Virtual, Navigation, Pagination]}
        onSwiper={setSwiperRef}
        slidesPerView={3}
        centeredSlides={true}
        spaceBetween={30}
        pagination={{
          type: 'fraction',
        }}
        navigation={true}
        virtual
      >
        {slides.map((slide, index) => (
          <SwiperSlide key={slide.id} virtualIndex={index}>
            <div className="slideContent">
              <img src={slide.image} alt={slide.title} className="slideImg" />
              <h3>{slide.title}</h3>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </>
  );
};

export default SpecialtySlide;
