// import style from '../css/RegisterPage.module.css';
import '../styles/LoginPage.scss'
import { useState } from 'react';
import { Link, Navigate } from 'react-router-dom';
import { url } from '../store/ref';
// import KakaoLogin from '../components/KakaoLogin';

const LoginPage = () => {
  const [userID, setUserID] = useState('');
  const [password, setPassword] = useState('');
  const [message1, setMessage1] = useState('');
  const [message2, setMessage2] = useState('');
  const [redirect, setRedirect] = useState(false);

  const login = async (e) => {
    e.preventDefault();
    console.log(userID, password);

    if (!/^[a-zA-Z][a-zA-Z0-9]{3,}$/.test(userID)) {
      setMessage1('아이디는 4자 이상이어야 하며 영어로 시작해야 합니다.');
      return;
    } else {
      setMessage1('');
    }
    if (password.length < 4) {
      setMessage2('4자 이상이어야 합니다.');
      return;
    } else {
      setMessage2('');
    }

    const response = await fetch(`${url}/login`, {
      method: 'POST',
      body: JSON.stringify({ userID, password }),
      headers: { 'Content-Type': 'application/json' },
      credentials: 'include',
    });

    const data = await response.json();
    console.log(data);

    if (data.id) {
      setRedirect(true);
    }
    if (data.message === 'nouser') {
      setMessage1('사용자가 없습니다.');
    }
    if (data.message === 'failed') {
      setMessage2('비밀번호가 맞지 않습니다.');
    }
  };



  if (redirect) {
    return <Navigate to="/" />;
  }

  return (
    <main className={`mw register`}>
      <h1>Sign in</h1>

      <form onSubmit={login}>
        <input
          type="text"
          placeholder="아이디"
          value={userID}
          onChange={(e) => {
            setUserID(e.target.value);
          }}
        />
        <span>{message1}</span>
        <input
          type="password"
          placeholder="패스워드"
          value={password}
          onChange={(e) => {
            setPassword(e.target.value);
          }}
        />
        <span>{message2}</span>
        <button type="submit" className="btn-secondary">로그인</button>
        <div className="socialLogin">
          {/* <KakaoLogin/> */}
        </div>
      </form>

      <p>계정이 없나요?</p>
        계정이 없나요?<Link to="/register">회원가입</Link>
      
    </main>
  );
};

export default LoginPage;
