import style from '../css/CommentsLi.module.css';

const CommentsLi = () => {
  return (
    <li>
      <div className={style.list}>
        <p className={style.content}>댓글입력내용</p>
        <p className={style.author}>작성자</p>
        <p className={style.date}>날자</p>
        <button>수정</button>
        <button>삭제</button>
      </div>
      <div style={{ display: 'none' }}>
        <textarea name="" id=""></textarea>
        <button>수정작성완료</button>
        <button>취소</button>
      </div>
    </li>
  );
};

export default CommentsLi;
