import style from '../css/CommentsArea.module.css';

const CommentsArea = () => {
  return (
    <section className={style.CommemtsArea}>
      <h3>댓글 영역</h3>
      <div>
        <textarea name="content" id="content"></textarea>
        <button>댓글 입력</button>
      </div>
      <ul>
        <li>
          <div>
            <p>댓글입력내용</p>
            <p>작성자</p>
            <p>날자</p>
            <button>수정</button>
            <button>삭제</button>
          </div>


        </li>
      </ul>
    </section>
  );
};

export default CommentsArea;
