import { combineReducers } from 'redux';

// 초기 상태 설정
const initialState = {
  user: null,
};

// 리듀서 함수 정의
function userReducer(state = initialState, action) {
  switch (action.type) {
    case 'SET_USER':
      return {
        ...state,
        user: action.payload,
      };
    default:
      return state;
  }
}

// 여러 리듀서를 결합하여 루트 리듀서 생성
const rootReducer = combineReducers({
  user: userReducer,
});

export default rootReducer;
