import { useEffect, useState } from 'react';
import PostCard from '../components/PostCard';
import '../styles/MainListPage.scss';
// 스와이퍼
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import { Autoplay, Pagination, Navigation } from 'swiper/modules';

import { url } from '../store/ref';

// 이미지 배열로 임포트
const images = [
  require('../assets/mainSlide/'),
  require('../assets/mainSlide/Slide2.png'),
  require('../assets/mainSlide/Slide3.png'),
  require('../assets/mainSlide/Slide4.png'),
];

const MainListPage = () => {
  const [postList, setPostList] = useState([]);

  useEffect(() => {
    fetch(`${url}/postList`)
      .then((res) => res.json())
      .then((data) => setPostList(data))
      .catch((error) => {
        console.error('Error fetching posts:', error);
      });
  }, []);

  return (
    <main className="mw mainList">
      <Swiper
        spaceBetween={30}
        centeredSlides={true}
        autoplay={{
          delay: 2500,
          disableOnInteraction: false,
        }}
        pagination={{
          clickable: true,
        }}
        navigation={true}
        modules={[Autoplay, Pagination, Navigation]}
        className="mySwiper"
      >
        {images.map((image, index) => (
          <SwiperSlide key={index}>
            <img src={image.default} alt={`Slide ${index + 1}`} />
          </SwiperSlide>
        ))}
      </Swiper>

      <h2>블로그 리스트 & 메인페이지</h2>

      <div className="postsCon">
        {postList.map((post) => (
          <PostCard key={post._id} post={post} />
        ))}
      </div>
    </main>
  );
};

export default MainListPage;
