import React, { useEffect, useMemo } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom'; // useLocation 제거
import { setUserAllInto } from '../store/userStore';
import { url } from '../store/ref';
import '../styles/Header.scss';
import signInIcon from '../assets/icons/SingIn.svg';
import logo from '../assets/logo.png';

const Header = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user.user); // 리덕스에서 유저 정보 가져오기
  console.log('',user);
  const userID = useMemo(() => (user ? user.userID : null), [user]);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        //서버에서 JWT 토큰을 사용해 로그인된 유저 정보를 확인
        const response = await fetch(`${url}/profile`, {
          credentials: 'include',
        });
        if (response.ok) {
          const userInfo = await response.json();
          // console.log(userInfo);
          dispatch(setUserAllInto(userInfo)); //유저 정보를 리덕스에 저장
        } else {
          console.error('Failed to fetch profile:', response.statusText);
        }
      } catch (error) {
        console.error('Failed to fetch profile:', error);
      }
    };

    if (!userID) {
      fetchProfile();
    }
  }, [dispatch, userID]);

  const logout = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${url}/logout`, {
        method: 'POST',
        credentials: 'include',
      });
      if (response.ok) {
        dispatch(setUserAllInto(null));
      } else {
        console.error('Failed to logout:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to logout:', error);
    }
  };

  return (
    <header className="mw">
      <nav>
        <h1>
          <Link to="/">
            <img src={logo} alt="Logo" />
          </Link>
        </h1>
        <ul>
          <div className="menu">
            <li>Recipes</li>
            <li>
                <Link to="/create">Share your recipe</Link>
              </li>
          </div>

          {userID ? (
            <div className="afterLogin">
              <li>
                <Link to="/mypage">{userID}님 입장</Link>
              </li>
              <li>
                <Link to="/" onClick={logout}>
                  로그아웃
                </Link>
              </li>

            </div>
          ) : (
            <li className="signIn">
              <img src={signInIcon} alt="Sign In" />
              <Link to="/login">Sign in</Link>
            </li>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default React.memo(Header);
