import { Link } from 'react-router-dom';
import style from '../css/RegisterPage.module.css';
import { useState } from 'react';

const RegisterPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [pdcon, setPdcon] = useState('');
  const [message1, setMessage1] = useState('');
  const [message2, setMessage2] = useState('');

  const register = (e) => {
    e.preventDefault();
    setMessage1('');
    setMessage2('');

    // username은 4자 이상이어야 하며 영어로 시작해야 합니다.
    if (!/^[a-zA-Z][a-zA-Z0-9]{3,}$/.test(username)) {
      setMessage1('아이디는 4자 이상이어야 하며 영어로 시작해야 합니다.');
      return;
    }

    if (password.length < 4) {
      setMessage2('패스워드가 4자 이상이어야 합니다.');
      return;
    }

    // password와 password 확인 일치 여부 확인
    if (password !== pdcon) {
      setMessage2('패스워드가 일치하지 않습니다.');
      return;
    }

    // 유효성 검사를 모두 통과하면 회원가입 처리 로직 실행
    console.log('회원가입 성공:', { username, password });
  };

  return (
    <main className={`mw ${style.register}`}>
      <h2>회원가입</h2>

      <form onSubmit={register}>
        <input
          type="text"
          placeholder="사용자이름"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          autoComplete="username"
        />
        {message1 && <span>{message1}</span>}

        <input
          type="password"
          placeholder="패스워드"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          autoComplete="new-password"
        />
        {message2 && <span>{message2}</span>}

        <input
          type="password"
          placeholder="패스워드 확인"
          value={pdcon}
          onChange={(e) => setPdcon(e.target.value)}
          autoComplete="new-password"
        />

        <button type="submit">가입하기</button>
      </form>

      <p>
        이미 회원가입 되었나요? <Link to="/login">로그인</Link>
      </p>
    </main>
  );
};

export default RegisterPage;
