import { useState } from 'react';

import style from '../css/CreatePage.module.css';

const CreatePage = () => {
  const [title, setTitle] = useState('');
  const [summary, setSummary] = useState('');
  const [files, setFiles] = useState('');
  // const [content, setContent] = useState('');
  const [message1, setMessage1] = useState('');
  const [message2, setMessage2] = useState('');
  const [message3, setMessage3] = useState('');

  const createNewPost = (e) => {
    e.preventDefault();

    if (title === '') {
      setMessage1('제목을 입력해 주세요');
      document.getElementById('title').focus();
      return;
    } else {
      setMessage1('');
    }

    if (summary === '') {
      setMessage2('요약 내용을 입력해 주세요');
      document.getElementById('title').focus();
      return;
    } else {
      setMessage2('');
    }
    if (files === null) {
      setMessage3('썸네일 이미지가 필요해요')
      document.getElementById('file').focus();
      return;
    } else {
      setMessage3('')
    }
  };

  return (
    <main className="mw">
      <h2>새글 등록 </h2>

      <form className={style.writeCon} onSubmit={createNewPost}>
        <label htmlFor="title">제목</label>
        <input
          type="text"
          name="title"
          id="title"
          placeholder="제목을 입력해 주세요"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <p>&nbsp;{message1}</p>

        <label htmlFor="title">요약내용</label>
        <input
          type="text"
          name="summary"
          id="summary"
          placeholder="요약내용을 입력해 주세요"
          value={summary}
          onChange={(e) => setSummary(e.target.value)}
        />
        <p>&nbsp;{message2}</p>

        <label htmlFor="title">첨부파일</label>
        <input
          type="file"
          name="file"
          id="file"
          placeholder="요약내용을 입력해 주세요"
          value={files}
          onChange={(e) => setFiles(e.target.files)}
        />
        <p>&nbsp;{message3}</p>

        <label htmlFor="content">내용</label>
        <div style={{ height: '400px' }}>텍스트 에디터</div>
        <button>포스트 등록</button>
      </form>
    </main>
  );
};
export default CreatePage;
