import { useState } from 'react';
import style from '../css/CommentsLi.module.css';
import user from '../store/userStore';

// 1. 로그인 된 유저만 댓글 입력란을 볼 수 있다.
// 1-2 로그인이 안된 상태라면 로그인하세요라는 메시지를 보여준다.
// 1-3 댓글 입력 후 버튼 클럭하면 api 백엔드 개발
// 1-4 전송되는 정보 content postid (username -cookie 정보로 활용 즉, 백엔드에서 처리)

// 2-1 댓글 수정하기 버튼을 클릭하면 수정 input 란이 등장
// 2-1 -1 input 영역에
// 2-2 기존 ui 안보이게
// 2-3 input 영역에 기존 댓글 입력된 상태

// 3-1 수정작성완료 버튼을 클릭하면 put 요청됨, api 백엔드 개발
// 3-2 취소 버튼을 클릭하면 이전 상태로 돌감. (기존 리스트 보여짐 state로 변경)
// 3-3 다른 취소 버튼을 클릭하면 모든 editCon는 이전 상태로 돌아감 ( state로 변경)

const CommentsLi = ({ comment, userName, setEditingCommentId }) => {
  //const [editingCommentId, setEditingCommentId] = useState(null); //상위 컴포넌트로 이동
  const [updateComment, setUpdateComment] = useState('');
  const day = new Date(comment.updatedAt);
  const date = day.toLocaleDateString();

  const editComment = (e) => {
    //수정버튼 클릭 시 수정 폼이 보여지는 함수
    // 수정버튼을 클릭한 댓글의 ID 값을 state 정보로 활용

    setEditingCommentId(e.target.parentNode.parentNode.dataset.id);
    setUpdateComment(e.target.parentNode.querySelector('p').textContent); //기존 작성 댓글 나타나도록
  };
  const deleteComment = () => {};

  return (
    <li data-id={comment._id}>
      {/* 삼항연산자 활용 */}
      {editingCommentId === comment._id ? (
        <div className={style.editCon}>
          <textarea
            name="content"
            id={comment._id}
            value={updateComment}
            onChange={(e) => {
              setUpdateComment(e.target.value);
            }}
          />
          <button>수정작성완료</button>
          <button>취소</button>
        </div>
      ) : (
        <div className={style.list}>
          <p className={style.content}>{comment.content}</p>
          <p className={style.author}>{comment.author}</p>
          <p className={style.date}>{date}</p>

          {userName === comment.author ? (
            <>
              <button onClick={editComment}>수정</button>
              <button onClick={deleteComment}>삭제</button>
            </>
          ) : (
            <>
              <button disabled>수정</button>
              <button disabled>삭제</button>
            </>
          )}
        </div>
      )}
    </li>
  );
};

export default CommentsLi;
