import React, { useState } from 'react';
import { Virtual, Pagination, Scrollbar } from 'swiper/modules';
import { Swiper, SwiperSlide } from 'swiper/react';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';

const SpecialtySlide = () => {
  const [swiperRef, setSwiperRef] = useState(null);

  const slides = [
    {
      id: 1,
      title: 'Strawberry Crepe',
      image: require('../assets/specialtySlide/specialty1.png'),
      backImage: require('../assets/specialtySlide/specialty1-back.png'), // 뒷면 이미지
    },
    {
      id: 2,
      title: 'Organic Market',
      image: require('../assets/specialtySlide/specialty2.png'),
      backImage: require('../assets/specialtySlide/specialty2-back.png'),
    },
    {
      id: 3,
      title: 'Pumpkin Soup',
      image: require('../assets/specialtySlide/specialty3.png'),
      backImage: require('../assets/specialtySlide/specialty3-back.png'),
    },
    // 추가 슬라이드들
  ];

  return (
    <>
      <Swiper
        modules={[Virtual, Pagination, Scrollbar]}
        onSwiper={setSwiperRef}
        slidesPerView={3.5}
        spaceBetween={40}
        scrollbar={{ draggable: true }}
        navigation={false}
        virtual
      >
        {slides.map((slide, index) => (
          <SwiperSlide key={slide.id} virtualIndex={index}>
            <div className="card-container">
              <div className="card">
                <div className="card-front">
                  <img src={slide.image} alt={slide.title} className="slideImg" />
                </div>
                <div className="card-back">
                  <img src={slide.backImage} alt={`Back of ${slide.title}`} className="slideImg" />
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </>
  );
};

export default SpecialtySlide;
