import { useParams } from 'react-router-dom';
import style from '../css/DetailPage.module.css';
import { url } from '../store/ref';

const DetailPage = () => {

  const {postID} = useParams()

  return (
    <main className={`mw ${style.detail}`}>
      <h2>상세페이지</h2>
      <section>
        <div className={style.detailimg}>
          <img src="https://picsum.photos/1200/800" alt="" />
          <h3>블로그 글 제목</h3>
        </div>
        <div className={style.info}>
          <p>작성자: username</p>
          <p>작성일: 2021-08-11</p>
        </div>
        <div className={style.summary}>블로그 요약 내용</div>
        <div className={style.desc}>상세내용</div>
      </section>
      <hr />
      <section className={style.btns}>
        {/* 로그인한 사용자만 글을 수정, 삭제할 수 있습니다. */}
        <button>수정</button>
        <button>삭제</button>
        <button>목록으로</button>
      </section>
      <hr />
      <section className="commentlist">댓글목록</section>
    </main>
  );
};

export default DetailPage;
