import React, { useState } from 'react';
import { Virtual, Pagination, Scrollbar } from 'swiper/modules';
import { Swiper, SwiperSlide } from 'swiper/react';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/scrollbar';

import '../styles/MainListPage.scss';

const SpecialtySlide = () => {
  const [swiperRef, setSwiperRef] = useState(null);

  const slides = [
    {
      id: 1,
      title: 'Greenery Salad',
      image: require('../assets/specialtySlide/specialty1.png'),
    },
    {
      id: 2,
      title: 'Egg Avocado Sandwich',
      image: require('../assets/specialtySlide/specialty2.png'),
    },
    {
      id: 3,
      title: 'Strawberry Pancakes',
      image: require('../assets/specialtySlide/specialty3.png'),
    },
    {
      id: 4,
      title: 'Ricotta Paprika Salad',
      image: require('../assets/specialtySlide/specialty4.png'),
    },
    {
      id: 5,
      title: 'Thanksgiving Chicken',
      image: require('../assets/specialtySlide/specialty5.png'),
    },
    {
      id: 6,
      title: 'Chow Mein',
      image: require('../assets/specialtySlide/specialty6.png'),
    },
    {
      id: 7,
      title: 'Pumpkin Soup',
      image: require('../assets/specialtySlide/specialty7.png'),
    },
  ];

  return (
    <>
      <Swiper
        modules={[Virtual, Pagination, Scrollbar]}
        onSwiper={setSwiperRef}
        slidesPerView={3.5}
        spaceBetween={40}
        autoHeight={true}
        scrollbar={{ draggable: true }}
        navigation={false}
        virtual
      >
        {slides.map((slide, index) => (
          <SwiperSlide key={slide.id} virtualIndex={index}>
            <div className="card-container">
              <div className="card">
                <div className="card-front">
                  <img
                    src={slide.image}
                    alt={slide.title}
                    className="slideImg"
                  />
                </div>
                <div className="card-back">
                  <h3 className="card-title">{slide.title}</h3>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </>
  );
};

export default SpecialtySlide;
