import style from '../css/CommentsArea.module.css';
import CommentsLi from './CommentsLi';
import { url } from '../store/ref';
import { useCallback, useEffect, useState } from 'react';

// V 1. 로그인 된 유저만 댓글 입력란을 볼 수 있다.
// V 1-2 로그인이 안된 상태라면 로그인하세요라는 메시지를 보여준다.
// 1-3 댓글 입력 후 버튼 클릭하면 api 백엔드 개발
// 1-4 전송되는 정보 content postid (username- cookie 정보로 활용 즉, 백엔드에서 처리
// 1-5 댓글리스트 불러옴. api get
// 1-6  <CommentsLi /> 바인딩 처리

const CommentsArea = ({ userName, postId }) => {
  const [comments, setComments] = useState();
  //console.log('comments---', comments);

  const fetchComments = useCallback(() => {
    fetch(`${url}/commentList/${postId}`) //
      .then((res) => res.json()) //
      .then((data) => setComments(data)); //
  }, [postId]);

  useEffect(() => {
    fetchComments();
  }, [fetchComments]);

  //댓글 등록
  const commentAdd = async () => {
    const content = document.getElementById('content').value;

    const data = new FormData();
    data.set('postId', postId);
    data.set('content', content);

    console.log(data);

    const response = await fetch(`${url}/commentAdd`, {
      method: 'POST',
      body: data,
      credentials: 'include', //쿠키 활용할때
    });
    console.log(response);
    if (response.ok) {
      console.log('댓글 입력 완료가 되면 db호출함수 재실행');
      fetchComments(); //db호출함수
      document.getElementById('content').value = '';
      document.getElementById('content').focus();
    }
  };

  return (
    <section className={style.commentsArea}>
      <h3>댓글영역</h3>
      {userName ? (
        <div>
          <textarea name="content" id="content"></textarea>
          <button onClick={commentAdd}>댓글입력</button>
        </div>
      ) : (
        <p>로그인 하면 댓글을 등록할 수 있습니다.</p>
      )}

      <hr />
      <ul>
        {comments?.map((comment) => {
          return (
            <CommentsLi
              key={comment._id}
              comment={comment}
              userName={userName}
            />
          );
        })}
      </ul>
    </section>
  );
};

export default CommentsArea;
