import style from '../css/CommentsArea.module.css';
import CommentsLi from './CommentsLi';
import { url } from '../store/ref';

//1. 로그인된 유저만 댓글 입력란을 볼 수 있다.

const CommentsArea = ({ userName, postId }) => {
  const commentAdd = () => {
    const content = document.getElementById('content').value;

    const data = new FormData();
    data.set('postId', postId);
    data.set('content', content);

    console.log(data);

    // fetch(`${url}/commentAdd`, {
    //   method: 'POST',
    //   headers: { ContentType: 'application/json' },
    //   body: JSON.stringify({
    //     postId,
    //     content,
    //   }),
    // }).then();
  };

  return (
    <section className={style.commentsArea}>
      <h3>댓글영역</h3>
      {userName ? (
        <div>
          <textarea name="content" id="content"></textarea>
          <button onClick={commentAdd}>댓글입력</button>
        </div>
      ) : (
        <p>로그인 하면 댓글을 등록할 수 있습니다.</p>
      )}

      <hr />
      <ul>
        <CommentsLi />
        <CommentsLi />
        <CommentsLi />
        <CommentsLi />
        <CommentsLi />
      </ul>
    </section>
  );
};

export default CommentsArea;
