import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import PostCard from '../components/PostCard';
import '../styles/MainListPage.scss';
import '../styles/App.scss';
import Slide from '../components/Slide.jsx';
import SpecialtySlide from '../components/SpecialtySlide.jsx';
import shareRecipeIMG from '../assets/MainListPage/shareRecipe/cooking.png';

import { url } from '../store/ref';

const MainListPage = () => {
  const [postList, setPostList] = useState([]);
  const navigate = useNavigate(); // useNavigate ÌõÖ Ï¥àÍ∏∞Ìôî

  useEffect(() => {
    fetch(`${url}/postList`)
      .then((res) => res.json())
      .then((data) => setPostList(data))
      .catch((error) => {
        console.error('Error fetching posts:', error);
      });
  }, []);

  return (
    <>
      <Slide />

      <main className="mw mainList">
        <p className="subTitle">NEW RECIPES</p>

        <div className="postsCon">
          {postList.map((post) => (
            <PostCard key={post._id} post={post} />
          ))}
        </div>
        <button
          className="btn-primary more"
          onClick={() => navigate('/Recipes')}
        >
          View More
        </button>
      </main>

      <section className="specialty">
        <div className="mw">
          <div className="spcialtyTitle">
            <h2>
              Today's <br />
              <p className="h1">Specialty</p>
            </h2>
          </div>
          <div className="specialtyCon">
            <SpecialtySlide />
          </div>
        </div>
      </section>

      <section className="shareRecipe mw">
        <div className="shareCon">
          <div className="shareTxt">
            <h2>Share Your Favorite Recipes! üç≤</h2>
            <p>
              Are you passionate about cooking and have a go-to recipe that
              never fails? We'd love to hear about it! Please share your recipe,
              to help others discover new and exciting flavors. Let‚Äôs spread the
              joy of cooking together!
            </p>
            <button
              className="btn-primary more"
              onClick={() => navigate('/create')}
            >
              View More
            </button>
          </div>
          <img src={shareRecipeIMG} alt="shareRecipe" />
        </div>
      </section>
    </>
  );
};

export default MainListPage;
