import React, { useEffect, useMemo } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useLocation } from 'react-router-dom';
import { setUserAllInto } from '../store/userStore';
import { url } from '../store/ref';

const Header = () => {
  const dispatch = useDispatch();
  const location = useLocation();

  const user = useSelector((state) => state.user.user);
  const username = useMemo(() => (user ? user.username : null), [user]);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch(`${url}/profile`, {
          credentials: 'include',
        });

        if (!response.ok) {
          // 응답 상태 코드가 200-299가 아닌 경우 에러 발생
          throw new Error(`Network response was not ok: ${response.statusText}`);
        }

        const userInfo = await response.json();
        dispatch(setUserAllInto(userInfo));
      } catch (error) {
        // 네트워크 요청이나 응답 처리 중 발생한 에러를 콘솔에 출력
        console.error('There was a problem with the fetch operation:', error);
      }
    };

    fetchProfile();
  }, [dispatch, location.pathname]);

  const logout = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${url}/logout`, {
        method: 'POST',
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`Network response was not ok: ${response.statusText}`);
      }

      dispatch(setUserAllInto(null));
    } catch (error) {
      console.error('There was a problem with the logout operation:', error);
    }
  };

  return (
    <header className="mw hd">
      <h1>
        <Link to="/">로고</Link>
      </h1>
      {username ? (
        <nav>
          <span>{username}님 입장</span>
          <Link to="/" onClick={logout}>
            로그아웃
          </Link>
          <Link to="/create">포스트작성</Link>
        </nav>
      ) : (
        <nav>
          <Link to="/login">로그인</Link>
          <Link to="/register">회원가입</Link>
        </nav>
      )}
    </header>
  );
};

export default React.memo(Header);
