import logo from '../assets/logo.png';

import React, { useEffect, useMemo } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useLocation } from 'react-router-dom';
import { setUserAllInto } from '../store/userStore';
import { url } from '../store/ref';

const Header = () => {
  const dispatch = useDispatch();
  const location = useLocation();

  const user = useSelector((state) => state.user.user);
  const userID = useMemo(() => (user ? user.userID : null), [user]);

  useEffect(() => {
    // 카카오 SDK 초기화
    // if (!window.Kakao.isInitialized()) {
    //   window.Kakao.init('f65dbab13ce0e288353d738bac59809b');
    // }

    const fetchProfile = async () => {
      try {
        const response = await fetch(`${url}/profile`, {
          credentials: 'include',
        });
        if (response.ok) {
          const userInfo = await response.json();
          // console.log('userinfo',userInfo);
          dispatch(setUserAllInto(userInfo));
        } else {
          console.error('Failed to fetch profile:', response.statusText);
        }
      } catch (error) {
        console.error('Failed to fetch profile:', error);
      }
    };
    fetchProfile();
  }, [dispatch, location.pathname]);

  const logout = (e) => {
    e.preventDefault();
    fetch(`${url}/logout`, {
      method: 'POST',
      credentials: 'include',
    })
      .then((response) => {
        if (response.ok) {
          dispatch(setUserAllInto(null));
        } else {
          console.error('Failed to logout:', response.statusText);
        }
      })
      .catch((error) => {
        console.error('Failed to logout:', error);
      });
  };

  return (
    <header className="mw hd">
      <h1>
        <Link to="/"><img src={logo} alt="Logo" /></Link>
      </h1>
      {userID ? (
        <nav>
          <Link to="/mypage">{userID}님 입장</Link>
          <Link to="/" onClick={logout}>
            로그아웃
          </Link>
          <Link to="/create">포스트작성</Link>
        </nav>
      ) : (
        <nav>
          <Link to="/login">로그인</Link>
          <Link to="/register">회원가입</Link>
        </nav>
      )}
    </header>
  );
};

export default React.memo(Header);
