import { useState } from 'react';
import style from '../css/CreatePage.module.css';
import Editor from './Editor';
import { url } from '../store/ref';

const CreatePage = () => {
  const [title, setTitle] = useState('');
  const [summary, setSummary] = useState('');
  const [files, setFiles] = useState(null); // 초기값을 null로 변경
  const [content, setContent] = useState('');
  const [message1, setMessage1] = useState('');
  const [message2, setMessage2] = useState('');
  const [message3, setMessage3] = useState('');
  // const [message4, setMessage4] = useState(''); // 추가된 message4 변수

  const createNewPost = (e) => {
    e.preventDefault();

    if (title === '') {
      setMessage1('제목을 입력해 주세요');
      document.getElementById('title').focus();
      return;
    } else {
      setMessage1('');
    }

    if (summary === '') {
      setMessage2('요약 내용을 입력해 주세요');
      document.getElementById('summary').focus(); // 올바른 필드로 포커스 이동
      return;
    } else {
      setMessage2('');
    }

    if (files === null || files.length === 0) {
      setMessage3('썸네일 이미지가 필요해요');
      document.getElementById('files').focus();
      return;
    } else {
      setMessage3('');
    }

    // 데이터 객체 생성 및 파일 확인
    const data = new FormData();
    data.set('title', title);
    data.set('summary', summary);
    data.append('content', content);
    data.append('files', files[0]);

    // 백엔드로 데이터 전송
    fetch(`${url}/postWrite`, {
      method: 'POST',
      body: data,
      credentials: 'include',
    });
  };

  return (
    <main className="mw">
      <h2>새글 등록</h2>

      <form className={style.writeCon} onSubmit={createNewPost}>
        <label htmlFor="title">제목</label>
        <input
          type="text"
          name="title"
          id="title"
          placeholder="제목을 입력해 주세요"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <p>&nbsp;{message1}</p>

        <label htmlFor="summary">요약내용</label>
        <input
          type="text"
          name="summary"
          id="summary"
          placeholder="요약내용을 입력해 주세요"
          value={summary}
          onChange={(e) => setSummary(e.target.value)}
        />
        <p>&nbsp;{message2}</p>

        <label htmlFor="files">첨부파일</label>
        <input
          type="file"
          name="files"
          id="files"
          onChange={(e) => setFiles(e.target.files)}
        />
        <p>&nbsp;{message3}</p>

        <label htmlFor="content">내용</label>
        <Editor content={content} setContent={setContent} />
        {/* <p>&nbsp;{message4}</p> */}
        <button>포스트 등록</button>
      </form>
    </main>
  );
};

export default CreatePage;
