import style from '../css/CreatePage.module.css';

const CreatePage = () => {
  const [title, setTitle] = useState('')
  const [summary, setSummary] = useState('')
  const [files, setFiles] = useState('')
  const [content, setCon] = useState('')


  return (
    <main className="mw">
      <h2>새글 등록 </h2>

      <form className={style.writeCon}>
        <label htmlFor="title">제목</label>
        <input
          type="text"
          name="title"
          id="title"
          placeholder="제목을 입력해 주세요"
          //value={'전송되는 값'}
        />

        <p>&nbsp;</p>

        <label htmlFor="title">요약내용</label>
        <input
          type="text"
          name="summary"
          id="summary"
          placeholder="요약내용을 입력해 주세요"
          //value={'전송되는 값'}
        />

        <p>요약관련 메세지</p>

        <label htmlFor="title">첨부파일</label>
        <input
          type="file"
          name="file"
          id="file"
          placeholder="요약내용을 입력해 주세요"
          //value={'전송되는 값'}
        />

        <p>파일관련 메세지</p>

        <label htmlFor="content">첨부파일</label>
        <div style={{height: '400px'}}>텍스트 에디터</div>

        <p>컨텐츠관련 메세지</p>
        <button>포스트 등록</button>
      </form>
    </main>
  );
};
export default CreatePage;
