import { useNavigate } from 'react-router-dom';
import styles from '../styles/PostCard.module.scss'; // 스타일을 모듈로 가져오기
import { url } from '../store/ref';

const PostCard = ({ post }) => {
  const navigate = useNavigate();
  const date = new Date(post.createdAt);
  const day = date.toLocaleDateString();
  const time = date.toLocaleTimeString([], {
    hour: '2-digit',
    minute: '2-digit',
  });

  return (
    <article
      className={styles.post} // 모듈화된 클래스 사용
      onClick={() => {
        navigate(`/detail/${post._id}`);
      }}
    >
      <div className={styles.postImg}> {/* 모듈화된 클래스 사용 */}
        <img src={`${url}/${post.cover}`} alt="샘플이미지" />
      </div>
      <div className={styles.info}> {/* 모듈화된 클래스 사용 */}
        <h3>{post.title}</h3>
        <p>{post.summary}</p>
        <p className=''>
          <span>{post.author}</span> / <span>{day}</span> <span>{time}</span>
        </p>
      </div>
    </article>
  );
};
export default PostCard;
