import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { setUserAllInto } from '../store/userStore';
import { url } from '../store/ref';
import '../styles/Header.scss';
import signInIcon from '../assets/icons/SingIn.svg';
import logo from '../assets/logo.png';

const Header = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user.user); // 리덕스에서 유저 정보 가져오기
  const userID = user ? user.userID : null;

  const [menuOpen, setMenuOpen] = useState(false); // 메뉴 열림/닫힘 상태

  const toggleMenu = () => {
    setMenuOpen(!menuOpen); // 메뉴 상태를 토글
  };

  const closeMenu = () => {
    setMenuOpen(false); // 메뉴를 닫음
  };

  const logout = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${url}/logout`, {
        method: 'POST',
        credentials: 'include',
      });
      if (response.ok) {
        dispatch(setUserAllInto(null)); // 로그아웃 시 유저 정보 초기화
        closeMenu(); // 로그아웃 후 메뉴 닫기
      } else {
        console.error('Failed to logout:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to logout:', error);
    }
  };

  return (
    <header>
      <nav>
        <h1>
          <Link to="/">
            <img src={logo} alt="Logo" />
          </Link>
        </h1>

        <button className="ham" onClick={toggleMenu}>
          <i className="fa-solid fa-bars" />
        </button>

        <ul className={menuOpen ? 'open' : 'mw'}>
          <div className="menu">
            <li onClick={closeMenu}>
              <Link to="/recipes">Recipes</Link>
            </li>
            <li onClick={closeMenu}>
              <Link to="/create">Share your recipe</Link>
            </li>
          </div>

          {userID ? (
            <div className="afterLogin">
              <li onClick={closeMenu}>
                <Link to="/mypage">{userID}님 입장</Link>
              </li>
              <li onClick={logout}>
                Logout
              </li>
            </div>
          ) : (
            <li className="signIn" onClick={closeMenu}>
              <img src={signInIcon} alt="Sign In" />
              <Link to="/login">Sign in</Link>
            </li>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default React.memo(Header);
